import React from "react";
import Form from "./common/form";
import { Joi } from "joi-browser";

class Quote extends Form {
  state = {
    data: {
      origin: "",
      destination: "",
      outbound: "",
      inbound: "",
      currency: ""
    },
    errors: {}
  };
  schema = {
    origin: Joi.string()
      .required()
      .label("Origin"),
    destination: Joi.string().required.label("Destination"),
    outbound: Joi.string().required.label("Outbound"),
    currency: Joi.string().required.label("Currency"),
    inbound: Joi.string().label("Inbound")
  };
  render() {
    return (
      <React.Fragment>
        <h1>Where to next</h1>
        <form onSubmit={this.handleSubmit}></form>
        {this.renderInput("origin", "Origin")}
        {this.renderInput("destination", "Destination")}
        {this.renderInput("currency", "Currency")}
        {this.renderInput("outbound", "Outbound date", "date")}
        {this.renderInput("inbound", "Inbound date (Optional)", "date")}
      </React.Fragment>
    );
  }
}

export default Quote;
