import React from "react";
import Joi from "joi-browser";
import Form from "./common/form";

class LoginForm extends From {
  state = { data: { email: "", password: "" }, errors: {} };

  schema = {
    email: Joi.string()
      .required()
      .label("Email")
      .email(),
    password: Joi.string()
      .required()
      .label("Password")
  };
  doSubmit = () => {
    //call the server
    consolelog("Submitted");
  };
  render() {
    return (
      <div>
        <h1>Login</h1>
      </div>
    );
  }
}

export default LoginForm;
