import React from "react";
import Form from "./common/form";
import Joi from "joi-browser";
import { getQuote } from "../services/quoteService";
import { Modal, Button } from "react-bootstrap";

class Quote extends Form {
  state = {
    data: {
      origin: "",
      destination: "",
      outbound: "",
      currency: "",
      inbound: ""
    },
    errors: {}
  };
  schema = {
    origin: Joi.string()
      .required()
      .label("Origin"),
    destination: Joi.string()
      .required()
      .label("Destination"),
    outbound: Joi.string()
      .required()
      .label("Outbound"),
    currency: Joi.string()
      .required()
      .label("Currency"),
    inbound: Joi.string().label("Inbound")
  };
  doSubmit = async () => {
    const { data } = this.state;
    await getQuote(data);
  };
  renderDialog() {
    const [show, setShow] = React.useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    return (
      <div>
        <Button
          variant="primary"
          onClick={handleShow}
          disabled={this.validate()}
        >
          Serarch Quote ->
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>What a bargain</Modal.Title>
          </Modal.Header>
          <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={handleClose}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    );
  }
  render() {
    return (
      <React.Fragment>
        <h1>Where to next</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("origin", "Origin")}
          {this.renderInput("destination", "Destination")}
          {this.renderInput("currency", "Currency")}
          {this.renderInput("outbound", "Outbound date", "date")}
          {this.renderInput("inbound", "Inbound date (Optional)", "date")}
          {this.renderDialog()}
        </form>
      </React.Fragment>
    );
  }
}

export default Quote;
