import React from "react";
import Joi from "joi-browser";
import Form from "./common/form";

class LoginForm extends From {
  state = { data: { email: "", password: "" }, errors: {} };

  schema = {
    email: Joi.string()
      .required()
      .label("Email")
      .email(),
    password: Joi.string()
      .required()
      .label("Password")
  };

  render() {
    return <React.Fragment></React.Fragment>;
  }
}

export default LoginForm;
